mapper.insert:
INSERT INTO TB_REQUEST
	(
	REQ_NO
  , REQ_DATE
  , REQ_MEMBER_ID
  	)
  VALUES
  (
  SEQ_REQ_NO.NEXTVAL
, #{ reqDate }
, #{ memberId }
  )

mapper.select:
SELECT A.RNUM , A.REQ_NO , A.REQ_APROV_STATUS , A.REQ_DATE , A.REQ_MEMBER_ID FROM (SELECT ROWNUM RNUM , B.REQ_NO , B.REQ_APROV_STATUS , B.REQ_DATE , B.REQ_MEMBER_ID FROM (SELECT C.REQ_NO , C.REQ_APROV_STATUS , C.REQ_DATE , C.REQ_MEMBER_ID FROM TB_REQUEST C
WHERE REQ_MEMBER_ID LIKE '%' || #{ searchValue } || '%'
)B
WHERE ROWNUM <= #{ endRow }
) A WHERE A.RNUM >= #{ startRow }

mapper.update:
UPDATE TB_REQUEST
   SET REQ_APROV_STATUS = '승인'
 WHERE REQ_NO = ${ reqNo }

UPDATE TB_REQUEST
   SET REQ_APROV_STATUS = '거절'
 WHERE REQ_NO = ${ reqNo }
